var undoStack = [];

function mkUndo (toolName) {
    var undoSelector = '#' + toolName + '-submit-undo';
    var textSelector = '#' + toolName + '-text';

    $j(undoSelector).click(function (ev) {
        ev.preventDefault();

        var last = undoStack.pop();
        $j(textSelector).val(last);

        if (undoStack.length == 0) {
            $j(undoSelector).hide();
        }
    });
}

function mkTool (toolName, computeFn, opts) {
    opts = opts || {};

    var submitSelector = '#' + toolName + '-submit';
    var undoSelector = '#' + toolName + '-submit-undo';
    var textSelector = '#' + toolName + '-text';

    $j(submitSelector).click(function () {
        var text = $j(textSelector).val();
        if (!opts.allowEmptyText) {
            if (!text.length) return;
        }

        $j('#action-error').hide();

        try {
            if (opts.asyncResultFn) {
                computeFn(text, opts.asyncResultFn);
            }
            else {
                var result = computeFn(text, opts.asyncResultFn);
                $j(textSelector).val(result);
            }
        }
        catch (err) {
            if (opts.exceptionFn) opts.exceptionFn(err);
            return;
        }

        undoStack.push(text);

        $j(undoSelector).show();
    });

    mkUndo(toolName);
}

function mkImageConvertTool (toolName, inputOpts, outputOpts, computeFn) {
    if ($j('#tool-' + toolName).length == 0) return;

    var fileSelector = '#file-select';
    var submitSelector = '#submit';
    var selectedFile;

    // make file selector work
    $j(fileSelector).on('change', function (ev) {
        $j('#action-error').hide();
        var file = ev.target.files[0];
        if (file.type != inputOpts.inputMime) {
            $j('#action-error').show();
            $j('#action-error').text("Selected file is not a " + inputOpts.inputHumanFormat);
            return;
        }
        selectedFile = file;
        $j(submitSelector).attr('disabled', false);
    });

    // make drag & drop work
    $j('#drag-and-drop').on('dragover', function (ev) {
        ev.preventDefault();
        ev.stopPropagation();
        $j('#drag-and-drop').addClass('hover');
    });
    $j('#drag-and-drop').on('dragenter', function (ev) {
        ev.preventDefault();
        ev.stopPropagation();
        $j('#drag-and-drop').addClass('hover');
    });
    $j('#drag-and-drop').on('dragleave', function (ev) {
        ev.preventDefault();
        ev.stopPropagation();
        $j('#drag-and-drop').removeClass('hover');
    });
    $j('#drag-and-drop').on('dragend', function (ev) {
        ev.preventDefault();
        ev.stopPropagation();
        $j('#drag-and-drop').removeClass('hover');
    });
    $j('#drag-and-drop').on('drop', function (ev) {
        ev.preventDefault();
        ev.stopPropagation();
        $j('#drag-and-drop').removeClass('hover');
        $j('#action-error').hide();
        ev.dataTransfer = ev.originalEvent.dataTransfer;
        var file = ev.dataTransfer.files[0];
        if (file.type != inputOpts.inputMime) {
            $j('#action-error').show();
            $j('#action-error').text("Selected file is not a " + inputOpts.inputHumanFormat);
            return;
        }
        $j('#drag-and-drop-selected').text("Selected " + file.name);
        selectedFile = file;
        $j(submitSelector).attr('disabled', false);
    });

    // make convert button work
    //
    $j(submitSelector).click(function () {
        var reader = new FileReader();
        reader.onload = function () {
            var img = new Image;
            img.onload = function () {
                var canvas = $j('<canvas>')[0];
                canvas.width = img.width;
                canvas.height = img.height;
                var canvasCtx = canvas.getContext('2d');
                canvasCtx.drawImage(img, 0, 0);
                function blobHandler (blob) {
                    var lastDot = selectedFile.name.lastIndexOf('.');
                    if (lastDot != -1) {
                        var outputFileName = selectedFile.name.substring(0, lastDot) + '.' + outputOpts.outputExt;
                    }
                    else {
                        var outputFileName = selectedFile.name + '.' + optputOpts.outputExt;
                    }
                    saveAs(blob, outputFileName);
                }
                canvas.toBlob(blobHandler, outputOpts.outputMime);
            }
            img.src = reader.result;
        }
        reader.readAsDataURL(selectedFile);
    });
}

$j(function () {
    // make copy to clipboard work
    //
    $j('#copy-to-clipboard').click(function (ev) {
        ev.preventDefault();
        $j('#tool-implementation textarea').select();
        document.execCommand('copy');
    });
});
